import * as actionTypes from "../actions/actionTypes";
import initalState from "./initalState";

export default function cartReducer(state=initalState.cart,action) {
  switch (action.type) {
    case actionTypes.ADD_TO_CART:
      var addedItem = state.find(
        (c) => c.product.productID === action.payload.product.productID
      );
      if (addedItem) {
        var newState = state.map((cartItem) => {
          debugger;
          if (
            cartItem.product.productID === action.payload.product.productID
          ) {
            return Object.assign({}, addedItem, {
              quantity: addedItem.quantity + 1,
            });
          }
          return cartItem;  
        }); 
        return newState;
      }else{
          return [...state,{...action.payload}] //state kopyas覺n覺 al action.payload ekle
      }

    default:
      return state;
  }
}
