import React, { Component } from "react";
import { connect } from "react-redux";
import {bindActionCreators} from "redux";
import * as categoryAction from "../../redux/actions/categoryAction"

class CategoryList extends Component {
  componentDidMount(){
    this.props.actions.getCategories()
  }
  render() {
    return (
      <div>
        <h3>Categories : {this.state.categories.length} </h3>
        <h5>Se√ßili Kategori : {this.props.currentCategory.categoryName}</h5>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentCategory: state.changeCategoryReducer,
    categories : state.CategoryListReducer
  };
}

function mapDispatchToProps(dispatch){
  return{
    actions :{
      getCategories : bindActionCreators(categoryAction.getCategories,dispatch)
    }
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(CategoryList)
