import React from "react";
import SelectInput from "../toolbox/SelectInput";
import TextInput from "../toolbox/TextInput";

const ProductDetail = ({categories, product, onSave, onChange}) => {
  return (
    <form onSubmit={onSave}>
      <h2>{product.productID ? "Güncelle" : "Ekle"}</h2>

      <TextInput
        name="productName"
        label="Product Name"
        values={product.name}
        onChange={onChange}
        error="ERROR"
      ></TextInput>
      <TextInput
        name="unitPrice"
        label="Unit Price"
        values={product.unitPrice}
        onChange={onChange}
        error="ERROR"
      ></TextInput>
      <TextInput
        name="quantityPerUnit"
        label="Quantity Per Unit"
        values={product.quantityPerUnit}
        onChange={onChange}
        error="ERROR"
      ></TextInput>
      <TextInput
        name="unitsInStock"
        label="Units In Stock"
        values={product.unitsInStock}
        onChange={onChange}
        error="ERROR"
      ></TextInput>

      <SelectInput
        name="categoryID"
        label="Category"
        values={product.categoryID || ""}
        defaultOption="Select"
        options={categories.map((category) => ({
          value: category.categoryID,
          text: category.name,
        }))}
        onChange={onChange}
        error="ERROR"
      ></SelectInput>
      <button type="submit" className="btn btn-success">
        Save
      </button>
    </form>
  );
};

export default ProductDetail;
