import React, { Component } from "react";
import { connect } from "react-redux";
import { ListGroup, ListGroupItem } from "reactstrap";
import { bindActionCreators } from "redux";
import * as catagoryActions from "../../redux/actions/catagoryActions";
class CatagoryList extends Component {
  componentDidMount() {
    this.props.actions.getCatagories();
  }
  render() {
    return (
      <div>
        <h3>catagories </h3>
        <ListGroup>
          {this.props.catagories.map((catagory) => (
            <ListGroupItem key={catagory.categoryID}>{catagory.Name}</ListGroupItem>
          ))}
        </ListGroup>
        <h5> secili katagori :{this.props.currentCatagory.catagoryName}</h5>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentCatagory: state.changeCatagoryReducer,
    catagories: state.catagoryListReducer,
  };
}
function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getCatagories: bindActionCreators(catagoryAction.getCatagories, dispatch),
    },
  };
}
export default connect(mapStateToProps, mapDispatchToProps)(CatagoryList);
