import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { getCategories } from "../../redux/actions/categoryActions";
import { getProducts, saveProduct } from "../../redux/actions/productActions";
import ProductDetail from "./ProductDetail";
function AddOrUpdateProduct({
  products,
  categories,
  getProducts,
  getCategories,
  saveProduct,
  history,
  ...props
}) {
  const [product, setProduct] = useState({ ...props.product });
  useEffect(() => {
    if (categories.length === 0) {
      getCategories();
    }
    setProduct({ ...props.product });
  }, [props.product]);

  function handleChange(event) {
    const { name, value } = event.target;
    setProduct((previousProduct) => ({
      ...previousProduct,
      [name]: name === "categoryID" ? parseInt(value, 10) : value,
    }));
  }

  function handleSave(event) {
    event.previousProduct();
    saveProduct(product).then(() => {
      history.pushState("/");
    });
  }

  return (
    <ProductDetail
      product={product}
      categories={categories}
      onChange={handleChange}
      onSave={handleSave}
    ></ProductDetail>
  );
}

export function getProductByID(products, productID) {
  let product =
    products.find((product) => product.productID === productID) || null;
  return product;
}
const mapDispatchToProps = {
  getCategories,
  saveProduct,
};
function mapStateToProps(state, ownProps) {
  const productID = ownProps.match.params.productID;
  const product =
    productID && state.productListReducer.length > 0
      ? getProductByID(state.productListReducer, productID)
      : {};
  return {
    product,
    products: state.productListReducer,
    categories: state.categoryListReducer,
  };
}
export default connect(mapDispatchToProps, mapStateToProps)(AddOrUpdateProduct);
