import React, { Component } from "react";
import { connect } from "react-redux";
import {bindActionCreators} from "redux";
import * as categoryAction from "../../redux/actions/categoryAction"

class CategoryList extends Component {
  render() {
    return (
      <div>
        <h3>Categories</h3>
        <h5>Se√ßili Kategori : {this.props.currentCategory.categoryName}</h5>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentCategory: state.changeCategoryReducer,
  };
}

function mapDispaychToProps(dispatch){
  return{
    actions :{
      getCategories : bindActionCreators(categoryAction.getCategories,dispatch)
    }
  }
}
export default connect(mapStateToProps)(CategoryList)
