import React, { Component } from "react";
import { connect } from "react-redux";
import { ListGroup, ListGroupItem } from "reactstrap";
import {bindActionCreators} from "redux";
import * as categoryAction from "../../redux/actions/categoryAction"
// {this.props.categories.length}
class CategoryList extends Component {
  componentDidMount(){
    this.props.actions.getCategories()
  }
  debugger;
  render() {
    return (
      <div>
        <h3>Categories </h3>  
        <ListGroup>
          {
            this.props.categories.map(category=>(
              <ListGroupItem> key={category.categoryID}
                  {category.categoryName}
          </ListGroupItem>

            ))
          }

          </ListGroup>  
        <h5>Se√ßili Kategori : {this.props.currentCategory.categoryName}</h5>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentCategory: state.changeCategoryReducer,
    categories : state.CategoryListReducer
  };

}

function mapDispatchToProps(dispatch){
  return{
    actions :{
      getCategories : bindActionCreators(categoryAction.getCategories,dispatch)
    }
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(CategoryList)
